CREATE OR REPLACE PROCEDURE pls_rescindir_sca_regra_contr ( nr_seq_regra_contr_p bigint, dt_rescisao_p timestamp, ie_sem_rescindir_benef_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


NR_SEQ_PLANO_sca_w			bigint;
nr_seq_contrato_w				bigint;
nr_seq_vinculo_sca_w			bigint;
nr_seq_plano_benef_w			bigint;
ds_historico_w				varchar(255);

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_sca_vinculo		b,
		pls_segurado		a
	where	b.nr_seq_segurado	= a.nr_sequencia
	and	b.nr_seq_plano		= nr_seq_plano_sca_w
	and	a.nr_seq_contrato	= nr_seq_contrato_w
	and	((pls_obter_produto_benef(a.nr_sequencia,dt_rescisao_p)	= nr_seq_plano_benef_w) or (coalesce(nr_seq_plano_benef_w::text, '') = ''))
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	coalesce(b.dt_fim_vigencia::text, '') = '';


BEGIN

select	nr_seq_plano,
	nr_seq_contrato,
	nr_seq_plano_benef
into STRICT	nr_seq_plano_sca_w,
	nr_seq_contrato_w,
	nr_seq_plano_benef_w
from	PLS_SCA_REGRA_CONTRATO
where	nr_sequencia	= nr_seq_regra_contr_p;

update	PLS_SCA_REGRA_CONTRATO
set	DT_FIM_VIGENCIA		= dt_rescisao_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_regra_contr_p;

if (ie_sem_rescindir_benef_p = 'S') then

	ds_historico_w	:= 'Rescisão do SCA  ' || substr(pls_obter_dados_produto(nr_seq_plano_sca_w,'N'),1,255) || ' do contrato e para os beneficiários do contrato a partir do dia ' ||
				to_char(dt_rescisao_p,'dd/mm/yyyy');

	open C01;
	loop
	fetch C01 into
		nr_seq_vinculo_sca_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL pls_rescindir_sca_benef(nr_seq_vinculo_sca_w,dt_rescisao_p,'N',nm_usuario_p);
		end;
	end loop;
	close C01;
else
	ds_historico_w	:= 'Rescisão do SCA  ' || substr(pls_obter_dados_produto(nr_seq_plano_sca_w,'N'),1,255) || ' do contrato a partir do dia ' || to_char(dt_rescisao_p,'dd/mm/yyyy');
end if;

insert into pls_contrato_historico(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		cd_estabelecimento,nr_seq_contrato,dt_historico,ie_tipo_historico,ds_historico)
values (		nextval('pls_contrato_historico_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		cd_estabelecimento_p,nr_seq_contrato_w,clock_timestamp(),'66',ds_historico_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
