CREATE OR REPLACE PROCEDURE pls_resp_se_lib_aud_liminar ( nr_seq_auditoria_p pls_auditoria.nr_sequencia%type, ie_lib_liminar_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
nr_seq_guia_w			pls_guia_plano.nr_sequencia%type;
ie_lib_texto_w			varchar(10);


BEGIN

begin
	select	nr_seq_requisicao,
		nr_seq_guia
	into STRICT	nr_seq_requisicao_w,
		nr_seq_guia_w
	from	pls_auditoria
	where	nr_sequencia = nr_seq_auditoria_p;
exception
when others then
	nr_seq_requisicao_w	:= null;
	nr_seq_guia_w		:= null;
end;

select	CASE WHEN ie_lib_liminar_p=0 THEN 'foi'  ELSE 'não foi' END
into STRICT	ie_lib_texto_w
;

if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
	CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_w, 'L', 'O auditor '||obter_nome_usuario(nm_usuario_p)|| ' informou que esta análise '||
						ie_lib_texto_w || ' liberada devido a liminar do beneficiário.',
						null, nm_usuario_p);
elsif (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
	CALL pls_guia_gravar_historico(nr_seq_guia_w,2,'O auditor '||obter_nome_usuario(nm_usuario_p)|| ' informou que esta análise '||
						ie_lib_texto_w || ' liberada devido a liminar do beneficiário.','',nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
