SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_restringir_ger_simul_preco ( nr_sequencia_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_contratacao_w		varchar(10);
nr_seq_classif_comercial_w	bigint;
nr_seq_regra_w			bigint;
qt_registros_w			bigint;
nr_seq_solicitacao_cliente_w	bigint;
ds_erro_w			varchar(255);


BEGIN

if (ie_opcao_p = 'S') then
	select	max(NR_SEQ_CLASSIFICACAO),
			max(ie_tipo_contratacao)
	into STRICT	nr_seq_classif_comercial_w,
		ie_tipo_contratacao_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_sequencia_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_restricao_simulacao
	where	((ie_tipo_contratacao	= ie_tipo_contratacao_w) or (coalesce(ie_tipo_contratacao::text, '') = ''))
	and	((nr_seq_classif_comercial = nr_seq_classif_comercial_w) or (coalesce(nr_seq_classif_comercial::text, '') = ''))
	and	ie_situacao		= 'A';

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
		select	count(1)
		into STRICT	qt_registros_w
		from	PLS_RESTRICAO_SIMUL_LIB
		where	nr_seq_regra		= nr_seq_regra_w
		and	((nm_usuario_liberacao	= nm_usuario_p) or (coalesce(nm_usuario_liberacao::text, '') = ''))
		and	((cd_perfil		= wheb_usuario_pck.get_cd_perfil) or (coalesce(cd_perfil::text, '') = ''));

		if (qt_registros_w = 0) then
			ds_erro_w	:= 	wheb_mensagem_pck.get_texto(281083);

		end if;
	end if;
elsif (ie_opcao_p = 'P') then

	select	max(nr_seq_solicitacao)
	into STRICT	nr_seq_solicitacao_cliente_w
	from	pls_comercial_cliente
	where	nr_sequencia	= nr_sequencia_p;

	select	max(NR_SEQ_CLASSIFICACAO),
			max(ie_tipo_contratacao)
	into STRICT	nr_seq_classif_comercial_w,
		ie_tipo_contratacao_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_seq_solicitacao_cliente_w;

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_restricao_simulacao
	where	((ie_tipo_contratacao	= ie_tipo_contratacao_w) or (coalesce(ie_tipo_contratacao::text, '') = ''))
	and	((nr_seq_classif_comercial = nr_seq_classif_comercial_w) or (coalesce(nr_seq_classif_comercial::text, '') = ''))
	and	ie_situacao		= 'A';

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
		select	count(1)
		into STRICT	qt_registros_w
		from	PLS_RESTRICAO_SIMUL_LIB
		where	nr_seq_regra		= nr_seq_regra_w
		AND	((nm_usuario_liberacao	= nm_usuario_p) or (coalesce(nm_usuario_liberacao::text, '') = ''))
		and	((cd_perfil		= wheb_usuario_pck.get_cd_perfil) or (coalesce(cd_perfil::text, '') = ''));

		if (qt_registros_w = 0) then
			ds_erro_w	:= 	wheb_mensagem_pck.get_texto(281084);

		end if;
	end if;

end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_restringir_ger_simul_preco ( nr_sequencia_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) FROM PUBLIC;

