SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_restringir_pj ( cd_cgc_p text, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE



qt_pj_sistema_w		bigint	:= 0;
ds_retorno_w		varchar(2);
cd_operadora_empresa_w	bigint;


BEGIN

select	max(cd_operadora_empresa)
into STRICT	cd_operadora_empresa_w
from	pessoa_juridica
where	cd_cgc	= cd_cgc_p;

if (coalesce(cd_operadora_empresa_w,0) <> 0) then
	select	count(*)
	into STRICT	qt_pj_sistema_w
	from	pessoa_juridica
	where	cd_operadora_empresa	= cd_operadora_empresa_w
	and	cd_cgc	<> cd_cgc_p;
end if;

if (qt_pj_sistema_w > 0) then
	ds_retorno_p	:= 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
