CREATE OR REPLACE PROCEDURE pls_restringir_plano_area ( nr_seq_plano_area_p bigint, nr_seq_plano_p bigint, cd_municipio_ibge_p text, sg_estado_p text, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE



qt_area_plano_w		bigint	:= 0;
ds_retorno_w		varchar(2);


BEGIN

if (coalesce(sg_estado_p,'0') <> '0') then
	select	count(*)
	into STRICT	qt_area_plano_w
	from	pls_plano_area
	where	sg_estado		= sg_estado_p
	and	nr_sequencia		<> nr_seq_plano_area_p
	and	nr_seq_plano		= nr_seq_plano_p;
elsif (coalesce(cd_municipio_ibge_p,'0') <> '0') then
	select	count(*)
	into STRICT	qt_area_plano_w
	from	pls_plano_area
	where	cd_municipio_ibge	= cd_municipio_ibge_p
	and	nr_sequencia		<> nr_seq_plano_area_p
	and	nr_seq_plano		= nr_seq_plano_p;
end if;

if (qt_area_plano_w > 0) then
	ds_retorno_p	:= 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
