CREATE OR REPLACE PROCEDURE pls_restringir_segmentacao ( ie_segmentacao_p text, nr_seq_rol_grupo_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE



qt_segmentacao_w	bigint	:= 0;
ds_retorno_w		varchar(2);


BEGIN

select	count(*)
into STRICT	qt_segmentacao_w
from	pls_rol_segmentacao
where	ie_segmentacao		= ie_segmentacao_p
and	nr_seq_rol_grupo	= nr_seq_rol_grupo_p;

if (qt_segmentacao_w > 0) then
	ds_retorno_p	:= 'S';
end if;

end;
$body$
