SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_retirar_protocolo_lote ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(2);


BEGIN

select	coalesce(max(b.ie_status),'P')
into STRICT	ie_status_w
from	pls_lote_protocolo	b,
	pls_protocolo_conta	a
where	a.nr_seq_lote		= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_protocolo_p;

if (ie_status_w	= 'D') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267018, null); /* O protocolo está vinculado a um lote de pagamento definitivo, operação cancelada. */
end if;

update	pls_prot_conta_titulo
set	nr_seq_lote		 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_protocolo	= nr_seq_protocolo_p;

update	pls_protocolo_conta
set	nr_seq_lote		 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_protocolo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
