CREATE OR REPLACE PROCEDURE pls_rn209_liberar_regra (nr_sequencia_p bigint, ie_opcao_p bigint) AS $body$
BEGIN

if (ie_opcao_p = 1) then
	begin
	update	ops_rn209_controle
	set 	dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

if (ie_opcao_p = 2) then
	begin
	update	ops_rn209_controle
	set 	dt_liberacao  = NULL
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
