CREATE OR REPLACE PROCEDURE pls_salvar_anexo_reinclusao ( nr_seq_reinclusao_p bigint, ds_caminho_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_segurado_w		pls_reinclusao_benef.nr_seq_segurado%type;
cd_estabelecimento_w		pls_reinclusao_benef.cd_estabelecimento%type;


BEGIN
if (ds_caminho_p IS NOT NULL AND ds_caminho_p::text <> '') then
	insert into pls_reinc_benef_anexo(	nr_sequencia, nr_seq_reinclusao, dt_anexo,
						ds_anexo, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec)
				values (	nextval('pls_reinc_benef_anexo_seq'), nr_seq_reinclusao_p, clock_timestamp(),
						ds_caminho_p, clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p);

	begin
	select	nr_seq_segurado,
		cd_estabelecimento
	into STRICT	nr_seq_segurado_w,
		cd_estabelecimento_w
	from	pls_reinclusao_benef
	where	nr_sequencia = nr_seq_reinclusao_p;
	exception
	when others then
		nr_seq_segurado_w	:= null;
		cd_estabelecimento_w	:= null;
	end;

	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then

		insert into pls_segurado_doc_arq(	nr_sequencia, cd_estabelecimento, ds_arquivo,
							dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
							nm_usuario_nrec, nr_seq_segurado, ie_origem_anexo,
							dt_documento, nm_arquivo, ie_apresenta_portal)
					values (	nextval('pls_segurado_doc_arq_seq'), cd_estabelecimento_w, ds_caminho_p,
							clock_timestamp(), clock_timestamp(), nm_usuario_p,
							nm_usuario_p, nr_seq_segurado_w, 'P',
							clock_timestamp(),'Reinclusão beneficiário - Portal Web.', 'S');
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
