CREATE OR REPLACE PROCEDURE pls_salvar_anex_alt_benef_web ( cd_pessoa_fisica_p text, nr_seq_tipo_documento_p bigint, ds_arquivo_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_pls_tipo_doc_w		bigint;
nr_seq_segurado_w		bigint;

C01 CURSOR FOR
	SELECT  nr_sequencia
	from	pls_segurado
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND nr_seq_tipo_documento_p IS NOT NULL AND nr_seq_tipo_documento_p::text <> '') then

	select	nextval('pls_tipo_documento_pf_seq')
	into STRICT	nr_seq_pls_tipo_doc_w
	;

	insert into pls_tipo_documento_pf(nr_sequencia, dt_atualizacao, nm_usuario,
				          dt_atualizacao_nrec,nm_usuario_nrec,dt_registro,
					  cd_pessoa_fisica, cd_estabelecimento, nr_seq_tipo_documento)
				   values (nr_seq_pls_tipo_doc_w, clock_timestamp(), nm_usuario_p,
					  clock_timestamp(), nm_usuario_p, clock_timestamp(),cd_pessoa_fisica_p,
					  cd_estabelecimento_p, nr_seq_tipo_documento_p);

	insert into pls_documento_pf(nr_sequencia, dt_atualizacao, nm_usuario,
				          dt_atualizacao_nrec,nm_usuario_nrec,cd_pessoa_fisica,
					  nr_seq_tipo_documento, ds_arquivo, nr_seq_tipo_doc_pf)
				   values (nextval('pls_documento_pf_seq'), clock_timestamp(), nm_usuario_p,
					  clock_timestamp(), nm_usuario_p, cd_pessoa_fisica_p,
					  nr_seq_tipo_documento_p, ds_arquivo_p, nr_seq_pls_tipo_doc_w);


	open C01;
	loop
	fetch C01 into
		nr_seq_segurado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			insert into pls_segurado_doc_arq(nr_sequencia, nr_seq_segurado, cd_estabelecimento, dt_atualizacao,
				 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_arquivo,
				 ie_origem_anexo, nr_seq_tipo_documento, ie_apresenta_portal
				)
			values (nextval('pls_segurado_doc_arq_seq'), nr_seq_segurado_w, cd_estabelecimento_p, clock_timestamp(),
				 nm_usuario_p, clock_timestamp(), nm_usuario_p, ds_arquivo_p,
				 'P', null, 'S'
				);
		end;
	end loop;
	close C01;

	commit;
end if;

end;
