CREATE OR REPLACE PROCEDURE pls_salvar_anex_rescis_seg_web ( nr_seq_segurado_p text, nr_seq_solicitacao_p text, nr_seq_tipo_documento_p bigint, ds_arquivo_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_apresenta_portal_p text) AS $body$
BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '' AND nr_seq_tipo_documento_p IS NOT NULL AND nr_seq_tipo_documento_p::text <> '') then
	/* Caso seja gerado pela nova rescisão, nr_seq_rescisão, senão é vinculado ao segurado  */

	if (nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') then
		insert into pls_solicitacao_resc_anexo(nr_sequencia, nr_seq_solicitacao, ds_arquivo,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
							nm_usuario_nrec, nr_seq_segurado, dt_anexo,
							ie_apresenta_portal)
						values (nextval('pls_solicitacao_resc_anexo_seq'), nr_seq_solicitacao_p, ds_arquivo_p,
							clock_timestamp(), nm_usuario_p, clock_timestamp(),
							nm_usuario_p, nr_seq_segurado_p, clock_timestamp(),
							ie_apresenta_portal_p);
	end if;

	insert into pls_segurado_doc_arq(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_segurado,
					cd_estabelecimento,
					nr_seq_tipo_documento,
					ds_arquivo,
					ie_origem_anexo,
					ie_apresenta_portal)
				values (	nextval('pls_segurado_doc_arq_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_segurado_p,
					cd_estabelecimento_p,
					nr_seq_tipo_documento_p,
					ds_arquivo_p,
					'P',
					ie_apresenta_portal_p);

	commit;
end if;

end;
$body$
