CREATE OR REPLACE PROCEDURE pls_salvar_compl_guia_web ( nr_seq_guia_p bigint, cd_doenca_p text, ie_tipo_saida_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Salvar o complemento da guia na requisição para autorização
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_requisicao_w	bigint;


BEGIN
if (cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') then
	 insert into pls_diagnostico(
			nr_sequencia, nr_seq_guia, cd_doenca,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, ie_classificacao)
                 values (
			nextval('pls_diagnostico_seq'), nr_seq_guia_p, upper(cd_doenca_p),
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			nm_usuario_p, 'P');
end if;

if (ie_tipo_saida_p IS NOT NULL AND ie_tipo_saida_p::text <> '') then
	update	pls_guia_plano
	set	ie_tipo_saida 	= ie_tipo_saida_p
	where	nr_sequencia 	= nr_seq_guia_p;
end if;


select 	coalesce(max(nr_seq_requisicao),0)
into STRICT	nr_seq_requisicao_w
from 	pls_execucao_req_item
where 	nr_Seq_guia = nr_seq_guia_p;

if (nr_seq_requisicao_w > 0) then
	CALL pls_salvar_compl_req_web(nr_seq_requisicao_w, upper(cd_doenca_p), ie_tipo_saida_p, nm_usuario_p, cd_estabelecimento_p);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
