CREATE OR REPLACE PROCEDURE pls_sca_regra_obito_beforepost ( nr_seq_tabela_p text, nr_seq_plano_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pls_plano
where	nr_sequencia = nr_seq_plano_p
and	ie_seguro_obito = 'B';

if (qt_existe_w = 0) then
	RAISE EXCEPTION '%', obter_texto_dic_objeto(135767,wheb_usuario_pck.get_nr_seq_idioma, null) USING ERRCODE = '45011';
end if;

if (nr_seq_tabela_p <> '') then

	select	count(*)
	into STRICT	qt_existe_w
	from	pls_tabela_preco
	where	nr_sequencia = nr_seq_tabela_p
	and	nr_seq_plano = nr_seq_plano_p;

	if (qt_existe_w = 0) then
		RAISE EXCEPTION '%', obter_texto_dic_objeto(135768,wheb_usuario_pck.get_nr_seq_idioma, null) USING ERRCODE = '45011';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
