CREATE OR REPLACE PROCEDURE pls_selecionar_item_atrib ( ie_tipo_tabela_p text, ie_tipo_preco_p text, nm_atributo_p text, ie_tipo_regra_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*  ie_tipo_regra_p
	0= Ordem da regra
	1= Exceção da regra*/
nr_seq_apresentacao_w	bigint;

BEGIN

if (nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') then
	if (ie_tipo_regra_p = 0) then
		begin
		select	max(nr_seq_apresentacao)+1
		into STRICT	nr_seq_apresentacao_w
		from	pls_regra_preco_ordem
		where	ie_tipo_tabela = ie_tipo_tabela_p;
		exception
		when others then
			nr_seq_apresentacao_w := 1;
		end;
	elsif (ie_tipo_regra_p = 1) then
		begin
		select	max(nr_seq_apresentacao)+1
		into STRICT	nr_seq_apresentacao_w
		from	pls_regra_preco_ordem_mat
		where	ie_tipo_tabela = ie_tipo_tabela_p;
		exception
		when others then
			nr_seq_apresentacao_w := 1;
		end;
	elsif (ie_tipo_regra_p = 2) then
		begin
		select	max(nr_seq_apresentacao)+1
		into STRICT	nr_seq_apresentacao_w
		from	pls_regra_preco_ordem_serv
		where	ie_tipo_tabela = ie_tipo_tabela_p;
		exception
		when others then
			nr_seq_apresentacao_w := 1;
		end;
	end if;

	if (coalesce(nr_seq_apresentacao_w::text, '') = '') then
		nr_seq_apresentacao_w := 1;
	end if;

	if (ie_tipo_regra_p = 0) then
		insert into	pls_regra_preco_ordem( 	cd_estabelecimento, dt_atualizacao, dt_atualizacao_nrec,
								ie_tipo_preco, ie_tipo_tabela, nm_atributo,
								nm_usuario, nm_usuario_nrec, nr_seq_apresentacao ,
								nr_sequencia)
					values (	cd_estabelecimento_p, clock_timestamp(), clock_timestamp(),
								ie_tipo_preco_p, ie_tipo_tabela_p, upper(nm_atributo_p),
								nm_usuario_p, nm_usuario_p, nr_seq_apresentacao_w ,
								nextval('pls_regra_preco_ordem_seq'));
	elsif (ie_tipo_regra_p = 1) then
		insert into	pls_regra_preco_ordem_mat( 	cd_estabelecimento, dt_atualizacao, dt_atualizacao_nrec,
									ie_tipo_preco, ie_tipo_tabela, nm_atributo,
									nm_usuario, nm_usuario_nrec, nr_seq_apresentacao ,
									nr_sequencia)
						values (	cd_estabelecimento_p, clock_timestamp(), clock_timestamp(),
									ie_tipo_preco_p, ie_tipo_tabela_p, upper(nm_atributo_p),
									nm_usuario_p, nm_usuario_p, nr_seq_apresentacao_w ,
									nextval('pls_regra_preco_ordem_mat_seq'));
	elsif (ie_tipo_regra_p = 2) then
		insert into	pls_regra_preco_ordem_serv( 	cd_estabelecimento, dt_atualizacao, dt_atualizacao_nrec,
									ie_tipo_preco, ie_tipo_tabela, nm_atributo,
									nm_usuario, nm_usuario_nrec, nr_seq_apresentacao ,
									nr_sequencia)
						values (	cd_estabelecimento_p, clock_timestamp(), clock_timestamp(),
									ie_tipo_preco_p, ie_tipo_tabela_p, upper(nm_atributo_p),
									nm_usuario_p, nm_usuario_p, nr_seq_apresentacao_w ,
									nextval('pls_regra_preco_ordem_serv_seq'));
	end if;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
