CREATE OR REPLACE PROCEDURE pls_sel_tabela_simulacao ( nr_seq_simul_perfil_p bigint, nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_preco_simul_tot_w		double precision;
nr_seq_simulacao_w		bigint;
vl_simulacao_perfil_w		double precision;


BEGIN

select	max(nr_seq_simulacao)
into STRICT	nr_seq_simulacao_w
from	pls_simulacao_perfil
where	nr_sequencia		= nr_seq_simul_perfil_p;

update	pls_simulacao_plano
set	ie_tabela_selecionada	= nr_seq_tabela_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_simul_perfil	= nr_seq_simul_perfil_p;

CALL pls_lancamento_sca_simulacao(nr_seq_simul_perfil_p,2,nm_usuario_p);

CALL pls_atualizar_valor_simul_col(nr_seq_simul_perfil_p,cd_estabelecimento_p,nm_usuario_p,'S');

commit;

end;
$body$
