SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_sib_confirmar_envio ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_movimento,
		a.ie_tipo_movimento,
		(	SELECT	max(x.nr_sequencia)
			from	pls_sib_segurado x
			where 	x.nr_seq_segurado = a.nr_seq_segurado) nr_seq_sib_segurado
	from	pls_sib_movimento a
	where	a.nr_seq_lote = nr_sequencia_p
	and	(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '')
	order by
		a.nr_seq_ordem_arquivo,
		a.ie_tipo_movimento;

BEGIN

for r_C01_w in C01 loop
	if (r_c01_w.ie_tipo_movimento = 1) then --Se for inclusão, precisa gerar um novo registro, pois na migração de contrato é copiado do beneficiário anterior
		delete	from	pls_sib_segurado
		where	nr_sequencia	= r_c01_w.nr_seq_sib_segurado;
		
		CALL pls_sib_segurado_pck.inserir_sib_segurado(r_C01_w.nr_seq_movimento,nm_usuario_p);
	else
		if (coalesce(r_c01_w.nr_seq_sib_segurado::text, '') = '') then
			CALL pls_sib_segurado_pck.inserir_sib_segurado(r_C01_w.nr_seq_movimento,nm_usuario_p);
		else
			CALL pls_sib_segurado_pck.alterar_sib_segurado(r_C01_w.nr_seq_movimento,nm_usuario_p);
		end if;
	end if;
end loop;

update	pls_sib_arquivo
set	dt_envio = clock_timestamp(),
	nm_usuario_envio = nm_usuario_p
where	nr_seq_lote = nr_sequencia_p;

update	pls_sib_lote
set	ie_status	= 4,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
