SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_sib_desfazer_confirmacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_lote_re_envio_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_lote_re_envio_w
from	pls_lote_sib
where	nr_seq_lote_envio  = nr_sequencia_p;


if (qt_lote_re_envio_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262442, 'NR_SEQ_LOTE=' || nr_sequencia_p);
	/* Mensagem: O lote NR_SEQ_LOTE possui sub-lotes! Favor verificar. */

else	update	pls_lote_sib
	set	dt_envio	 = NULL,
		ie_status	= 'A',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
