SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_sib_excluir_lote_conf ( nr_seq_lote_conf_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_conferencia_w	integer;


BEGIN
select	count(*)
into STRICT	qt_conferencia_w
from	pls_sib_conferencia
where	nr_seq_lote = nr_seq_lote_conf_p
and	(ie_tipo_movimento_reenvio IS NOT NULL AND ie_tipo_movimento_reenvio::text <> '');

if (qt_conferencia_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(817705); --Não é possível excluir o lote pois há registros marcados para re-envio
else
	delete	from pls_sib_conferencia
	where	nr_seq_lote = nr_seq_lote_conf_p;

	delete	from pls_sib_conferencia_arq
	where	nr_seq_lote = nr_seq_lote_conf_p;

	delete	from pls_sib_conferencia_lote
	where	nr_sequencia = nr_seq_lote_conf_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
