CREATE OR REPLACE PROCEDURE pls_sib_excluir_movimentacao ( nr_seq_lote_p bigint, nr_seq_movimento_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w	pls_sib_movimento.nr_seq_segurado%type;
ie_tipo_movimento_w	pls_sib_movimento.ie_tipo_movimento%type;
nr_seq_arquivo_w	pls_sib_movimento.nr_seq_arquivo%type;

BEGIN

select	nr_seq_segurado,
	ie_tipo_movimento,
	nr_seq_arquivo
into STRICT	nr_seq_segurado_w,
	ie_tipo_movimento_w,
	nr_seq_arquivo_w
from	pls_sib_movimento
where	nr_sequencia	= nr_seq_movimento_p;

insert into	pls_sib_log(	nr_sequencia, nr_seq_lote, ds_log,
		nr_seq_segurado, ie_tipo_movimento,
		dt_atualizacao, dt_atualizacao_nrec, nm_usuario, nm_usuario_nrec)
values (	nextval('pls_sib_log_seq'), nr_seq_lote_p, ds_observacao_p,
		nr_seq_segurado_w, ie_tipo_movimento_w,
		clock_timestamp(), clock_timestamp(), nm_usuario_p, nm_usuario_p);

delete	from pls_sib_movimento
where	nr_sequencia = nr_seq_movimento_p;

update	pls_sib_arquivo
set	qt_movimentacao	= qt_movimentacao-1
where	nr_sequencia	= nr_seq_arquivo_w
and	nr_seq_lote	= nr_seq_lote_p;

update	pls_sib_lote
set	qt_inclusao	= CASE WHEN ie_tipo_movimento_w=1 THEN qt_inclusao-1  ELSE qt_inclusao END ,
	qt_retificacao	= CASE WHEN ie_tipo_movimento_w=2 THEN qt_retificacao-1  ELSE qt_retificacao END ,
	qt_mudanca_contratual = CASE WHEN ie_tipo_movimento_w=3 THEN qt_mudanca_contratual-1  ELSE qt_mudanca_contratual END ,
	qt_cancelamento	= CASE WHEN ie_tipo_movimento_w=4 THEN qt_cancelamento-1  ELSE qt_cancelamento END ,
	qt_reativacao	= CASE WHEN ie_tipo_movimento_w=5 THEN qt_reativacao-1  ELSE qt_reativacao END ,
	qt_movimentacao	= qt_movimentacao-1
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
