SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_sib_gravar_cco ( nr_seq_devolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_segurado,
		cd_cco
	from	pls_sib_devolucao_cco
	where	nr_seq_devolucao = nr_seq_devolucao_p
	and	(nr_seq_segurado IS NOT NULL AND nr_seq_segurado::text <> '')
	and	(cd_cco IS NOT NULL AND cd_cco::text <> '')
	and	coalesce(dt_confirmacao::text, '') = '';

BEGIN

for r_c01_w in C01 loop
	begin

	update	pls_segurado
	set	cd_cco		= r_c01_w.cd_cco
	where	nr_sequencia	= r_c01_w.nr_seq_segurado;

	update	pls_sib_devolucao_cco
	set	dt_confirmacao		= clock_timestamp(),
		nm_usuario_confirmacao	= nm_usuario_p
	where	nr_sequencia	= r_c01_w.nr_sequencia;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
