CREATE OR REPLACE PROCEDURE pls_sib_reenviar_inclusao ( ie_reenviar_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_reenviar_w			varchar(1);
cd_carteira_benef_w		varchar(30);
nr_seq_segurado_w		bigint;


BEGIN

if (ie_reenviar_p	= 3) then
	ie_reenviar_w	:= 'I';
elsif (ie_reenviar_p	= 4) then
	ie_reenviar_w	:= 'M';
elsif (ie_reenviar_p	= 5) then
	ie_reenviar_w	:= 'N';
elsif (ie_reenviar_p	= 6) then
	ie_reenviar_w	:= 'E';
elsif (ie_reenviar_p	= 7) then
	ie_reenviar_w	:= 'A';
end if;

if (ie_reenviar_w	<> 'M') then
	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_sib_tasy_ans
	where	nr_sequencia	= nr_sequencia_p;

	select	max(cd_usuario_plano)
	into STRICT	cd_carteira_benef_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_w;
else
	select	cd_usuario_plano_ans
	into STRICT	cd_carteira_benef_w
	from	pls_sib_tasy_ans
	where	nr_sequencia	= nr_sequencia_p;
end if;

update	pls_sib_tasy_ans
set	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	ie_enviar_ans		= ie_reenviar_w,
	cd_usuario_plano	= cd_carteira_benef_w
where	nr_sequencia		= nr_sequencia_p;

commit;

end;
$body$
