CREATE OR REPLACE PROCEDURE pls_sib_reenviar_log_exclusao ( nr_seq_log_p pls_sib_log.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_segurado_w	pls_sib_log.nr_seq_segurado%type;
ie_tipo_movimento_w	pls_sib_log.ie_tipo_movimento%type;
ie_status_w		pls_sib_lote.ie_status%type;
qt_registro_w		integer;


BEGIN

if (nr_seq_log_p IS NOT NULL AND nr_seq_log_p::text <> '') then
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_sib_reenvio
	where	nr_seq_log_exclusao = nr_seq_log_p;

	if (qt_registro_w = 0) then
		select	a.nr_seq_segurado,
			a.ie_tipo_movimento,
			b.ie_status
		into STRICT	nr_seq_segurado_w,
			ie_tipo_movimento_w,
			ie_status_w
		from	pls_sib_log a,
			pls_sib_lote b
		where	b.nr_sequencia	= a.nr_seq_lote
		and	a.nr_sequencia	= nr_seq_log_p;

		if (ie_status_w = 4) then --Se o status do lote for Enviado
			insert	into	pls_sib_reenvio(	nr_sequencia, nr_seq_log_exclusao, ie_tipo_movimento, cd_estabelecimento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					nr_seq_segurado)
				values (	nextval('pls_sib_reenvio_seq'), nr_seq_log_p, ie_tipo_movimento_w, cd_estabelecimento_p,
					clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
					nr_seq_segurado_w);
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(853789); --Somente será possível reenviar a movimentação após a confirmação do envio do arquivo.
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
