CREATE OR REPLACE PROCEDURE pls_sib_reenvio_conf_cancel ( nr_seq_conferencia_p pls_sib_conferencia.nr_sequencia%type, nr_seq_motivo_cancel_p pls_sib_reenvio.nr_seq_motivo_cancel%type, dt_cancelamento_p pls_sib_reenvio.dt_cancelamento%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


qt_registro_w		integer;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_sib_reenvio
where	nr_seq_conferencia	= nr_seq_conferencia_p
and	ie_tipo_movimento	= 4
and	coalesce(nr_seq_lote_sib::text, '') = '';

if (qt_registro_w > 0) then --Já existe re-envio de #@DS_TIPO_MOVIMENTO#@ pendente para o registro de conferência. Favor verifique.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(834399,'DS_TIPO_MOVIMENTO='||obter_valor_dominio(8352,4));
else
	insert	into	pls_sib_reenvio(	nr_sequencia, nr_seq_conferencia, ie_tipo_movimento, cd_estabelecimento,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nr_seq_segurado, nr_seq_motivo_cancel, dt_cancelamento)
		values (	nextval('pls_sib_reenvio_seq'), nr_seq_conferencia_p, 4, cd_estabelecimento_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			null, nr_seq_motivo_cancel_p, dt_cancelamento_p);
end if;

commit;

end;
$body$
