CREATE OR REPLACE PROCEDURE pls_sib_reenvio_inclusao_benef ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


qt_registro_w		integer;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_sib_reenvio
where	nr_seq_segurado		= nr_seq_segurado_p
and	ie_tipo_movimento	= 1
and	coalesce(nr_seq_lote_sib::text, '') = '';

if (qt_registro_w > 0) then --Já existe re-envio de #@DS_TIPO_MOVIMENTO#@ pendente para o beneficiário. Favor verifique.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(993952,'DS_TIPO_MOVIMENTO='||obter_valor_dominio(8352,1));
else
	insert	into	pls_sib_reenvio(	nr_sequencia, nr_seq_segurado, ie_tipo_movimento, cd_estabelecimento,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nextval('pls_sib_reenvio_seq'), nr_seq_segurado_p, 1, cd_estabelecimento_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
