CREATE OR REPLACE PROCEDURE pls_sib_retificar_atributo ( nr_seq_lote_p pls_sib_conferencia_lote.nr_sequencia%type, nr_seq_conferencia_p pls_sib_conferencia.nr_sequencia%type, cd_atributo_p pls_sib_reenvio_atrib.cd_atributo%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


qt_registro_w		integer;
nr_seq_reenvio_w	pls_sib_reenvio.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_conferencia,
		nr_seq_segurado
	from	pls_sib_conferencia
	where	nr_seq_lote = nr_seq_lote_p
	and (nr_sequencia = nr_seq_conferencia_p or coalesce(nr_seq_conferencia_p::text, '') = '')
	and	(nr_seq_segurado IS NOT NULL AND nr_seq_segurado::text <> '');

BEGIN

for r_C01_w in C01 loop
	select	max(nr_sequencia)
	into STRICT	nr_seq_reenvio_w
	from	pls_sib_reenvio
	where	nr_seq_conferencia	= r_C01_w.nr_seq_conferencia
	and	ie_tipo_movimento	= 2 --Retificação
	and	coalesce(nr_seq_lote_sib::text, '') = '';

	if (coalesce(nr_seq_reenvio_w::text, '') = '') then
		insert	into	pls_sib_reenvio(	nr_sequencia, nr_seq_conferencia, ie_tipo_movimento, cd_estabelecimento,
				dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_segurado)
			values (	nextval('pls_sib_reenvio_seq'), r_C01_w.nr_seq_conferencia, 2, cd_estabelecimento_p,
				clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				r_c01_w.nr_seq_segurado)
			returning nr_sequencia into nr_seq_reenvio_w;
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_sib_reenvio_atrib
	where	nr_seq_reenvio = nr_seq_reenvio_w
	and	cd_atributo = cd_atributo_p;

	if (qt_registro_w = 0) then
		insert into pls_sib_reenvio_atrib(
						nr_sequencia,
						nr_seq_reenvio,
						cd_atributo,
						nm_usuario,
						dt_atualizacao,
						nm_usuario_nrec,
						dt_atualizacao_nrec)
		values (	nextval('pls_sib_reenvio_atrib_seq'),
			nr_seq_reenvio_w,
			cd_atributo_p,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp());
	end if;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
