CREATE OR REPLACE PROCEDURE pls_simulpreco_indiv_afterpost ( nr_sequencia_p bigint, nr_seq_preco_p bigint, ie_acao_executada_p bigint, ie_tipo_benef_p text, nr_seq_produto_p bigint, ie_alt_prod_dep_p text, qt_sca_bonif_tit_p INOUT text, qt_sca_bonif_dep_p INOUT text, nr_seq_titular_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_sequencia_w		bigint;
qt_scas_bonificacao_w	bigint;
qt_sca_bonif_depen_w	bigint;


BEGIN 
 
if (ie_acao_executada_p in (1,2)) then 
	--Gerar o lançamento automático da simulação 
	CALL pls_lancamento_sca_simulacao(nr_sequencia_p, 1, nm_usuario_p);
end if;
 
select	max(nr_sequencia) 
into STRICT	nr_sequencia_w 
from	pls_simulpreco_individual 
where	nr_seq_simulacao = nr_seq_preco_p 
and	ie_tipo_benef = 'T';
 
if (coalesce(nr_sequencia_w,0) > 0) and (ie_acao_executada_p in (1,2)) and (coalesce(nr_seq_produto_p,0) > 0) then 
	 
	if (ie_tipo_benef_p <> 'T') and (nr_sequencia_p <> nr_sequencia_w) then 
	 
		select	sum(qt_scas_bonificacao) 
		into STRICT	qt_sca_bonif_tit_p 
		from (	SELECT	count(*) qt_scas_bonificacao 
				from	pls_bonificacao_vinculo 
				where	nr_seq_segurado_simul = nr_sequencia_w 
				
union all
 
				SELECT	count(*) qt_scas_bonificacao 
				from	pls_sca_vinculo 
				where	nr_seq_segurado_simul = nr_sequencia_w) alias11;
		 
		select	sum(qt_scas_bonificacao) 
		into STRICT	qt_sca_bonif_dep_p 
		from (	SELECT	count(*) qt_scas_bonificacao 
				from	pls_bonificacao_vinculo 
				where	nr_seq_segurado_simul = nr_sequencia_p 
				
union all
 
				SELECT	count(*) qt_scas_bonificacao 
				from	pls_sca_vinculo 
				where	nr_seq_segurado_simul = nr_sequencia_p) alias3;
		nr_seq_titular_p	:= nr_sequencia_w;
	end if;
	 
	if (ie_alt_prod_dep_p = 'S') then 
		CALL pls_alt_produto_dep_sim_preco(nr_sequencia_w,nm_usuario_p);
	end if;	
end if;
 
commit;
 
end;
$body$
