CREATE OR REPLACE PROCEDURE pls_sip_tratar_campos_regras ( nr_seq_item_assist_p sip_item_assistencial.nr_sequencia%Type) AS $body$
DECLARE


/*

ESTA ROTINA É USADA PARA ATUALIZAR OS CAMPOS QUE FOREM DESATUALIZADOS NAS REGRAS PARA QUE  AS MESMAS NÃO PAREM DE FUNCIONAR !!!!

*/
qt_item_assist_w	bigint;

BEGIN

if (nr_seq_item_assist_p IS NOT NULL AND nr_seq_item_assist_p::text <> '') then
	begin
		select	count(1)
		into STRICT	qt_item_assist_w
		from	sip_item_assistencial a
		where	a.nr_sequencia = nr_seq_item_assist_p  LIMIT 1;
	exception
	when others then
		qt_item_assist_w := null;
	end;

	if (qt_item_assist_w IS NOT NULL AND qt_item_assist_w::text <> '') then
		if (qt_item_assist_w > 0) then

			update	sip_item_assist_regra_nv
			set	ie_nascido_vivo = 'N'
			where	nr_seq_item_assist = nr_seq_item_assist_p
			and (obter_se_habilita_regra_sip('ie_nascido_vivo', nr_seq_item_assist_p) = 'N');

			update	sip_item_assist_regra_nv
			set	ie_materiais = 'N'
			where	nr_seq_item_assist = nr_seq_item_assist_p
			and (obter_se_habilita_regra_sip('ie_materiais', nr_seq_item_assist_p) = 'N');

			update	sip_item_assist_regra_nv
			set	ie_considerar_conta = 'N'
			where	nr_seq_item_assist = nr_seq_item_assist_p
			and (obter_se_habilita_regra_sip('ie_considerar_conta', nr_seq_item_assist_p) = 'N');

			commit;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
