CREATE OR REPLACE PROCEDURE pls_solicita_acao_auditor ( nr_seq_auditoria_p bigint, nr_seq_acao_p bigint, ds_texto_solicitacao_p text, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_grupo_atual_w		bigint;
nr_seq_grupo_acao_w		bigint;
nr_seq_grupo_solicitado_w	bigint;
nr_seq_guia_w			bigint;
nr_seq_requisicao_w		bigint;
nr_seq_grupo_auditor_w		bigint;


BEGIN 
 
select	nr_sequencia, 
	nr_seq_grupo 
into STRICT	nr_seq_grupo_atual_w, 
	nr_seq_grupo_auditor_w 
from	pls_auditoria_grupo 
where	nr_sequencia = pls_obter_grupo_analise_atual(nr_seq_auditoria_p);
 
select	nr_seq_grupo_auditor 
into STRICT	nr_seq_grupo_acao_w 
from	pls_acao_analise_aut 
where	nr_sequencia = nr_seq_acao_p;
 
CALL pls_inserir_grupo_auditor(nr_seq_grupo_acao_w,nr_seq_auditoria_p,null,nm_usuario_p);
 
select	nr_seq_guia, 
	nr_seq_requisicao 
into STRICT	nr_seq_guia_w, 
	nr_seq_requisicao_w 
from	pls_auditoria 
where	nr_sequencia = nr_seq_auditoria_p;
 
if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then 
	insert into pls_guia_plano_historico(nr_sequencia, nr_seq_guia, ie_tipo_log, 
		dt_historico, dt_atualizacao, nm_usuario, 
		ds_observacao, ie_origem_historico, ie_tipo_historico, 
		nr_seq_grupo) 
	values (	nextval('pls_guia_plano_historico_seq'), nr_seq_guia_w, null, 
		clock_timestamp(), clock_timestamp(), nm_usuario_p, 
		ds_texto_solicitacao_p,'M','SGA', 
		nr_seq_grupo_auditor_w);
elsif (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then 
	insert into pls_requisicao_historico(nr_sequencia, nr_seq_requisicao, 
		dt_historico, dt_atualizacao, nm_usuario, 
		ds_historico, ie_origem_historico, ie_tipo_historico, 
		nr_seq_grupo) 
	values (	nextval('pls_requisicao_historico_seq'), nr_seq_requisicao_w, 
		clock_timestamp(), clock_timestamp(), nm_usuario_p, 
		ds_texto_solicitacao_p,'M','SGA', 
		nr_seq_grupo_auditor_w);
end if;
 
update	pls_auditoria_grupo 
set	dt_liberacao	= clock_timestamp(), 
	ie_status	= 'P', 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia	= nr_seq_grupo_atual_w;
 
select	pls_obter_grupo_analise_atual(nr_seq_auditoria_p) 
into STRICT	nr_seq_grupo_solicitado_w
;
 
update	pls_auditoria_grupo 
set	nr_seq_grupo_solic	= nr_seq_grupo_auditor_w, 
	nr_seq_acao_analise_aut = nr_seq_acao_p 
where	nr_sequencia		= nr_seq_grupo_solicitado_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
