CREATE OR REPLACE PROCEDURE pls_solicita_notif_atendimento ( nr_seq_auditoria_p bigint, ds_historico_notif_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_guia_w			bigint;
nr_seq_requisicao_w		bigint;
ds_historico_w			varchar(4000);
nr_seq_segurado_w		bigint;
nr_seq_notificacao_w		bigint;


BEGIN

select	nr_seq_segurado
into STRICT	nr_seq_segurado_w
from	pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;


update	pls_auditoria
set	ie_status	= 'AN',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where 	nr_sequencia 	= nr_seq_auditoria_p;

select	nextval('pls_notificacao_atend_seq')
into STRICT	nr_seq_notificacao_w
;

insert into pls_notificacao_atend(nr_sequencia, ie_status, dt_solicitacao,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_auditoria, nr_seq_segurado)
values (nr_seq_notificacao_w, 'AG', clock_timestamp(),
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, nr_seq_auditoria_p, nr_seq_segurado_w);

begin
select	coalesce(nr_seq_guia,0),
	coalesce(nr_seq_requisicao,0)
into STRICT	nr_seq_guia_w,
	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_guia_w		:= 0;
	nr_seq_requisicao_w	:= 0;
end;

ds_historico_w := substr('O auditor '||obter_nome_usuario(nm_usuario_p)|| ' solicitou notificação: '|| ds_historico_notif_p,1,4000);

if (nr_seq_guia_w	<> 0) then
	insert	into pls_guia_plano_historico(nr_sequencia, nr_seq_guia, dt_historico,
		 dt_atualizacao, nm_usuario, ds_observacao,
		 ie_origem_historico, ie_tipo_historico)
	values (nextval('pls_guia_plano_historico_seq'), nr_seq_guia_w, clock_timestamp(),
		 clock_timestamp(), nm_usuario_p, ds_historico_w,
		 'M', 'N');
elsif (nr_seq_requisicao_w	<> 0) then
	insert	into pls_requisicao_historico(nr_sequencia, nr_seq_requisicao, ie_tipo_historico,
		 ds_historico, dt_atualizacao, nm_usuario,
		 dt_historico, ie_origem_historico)
	values (nextval('pls_requisicao_historico_seq'), nr_seq_requisicao_w, 'N',
		 ds_historico_w, clock_timestamp(), nm_usuario_p,
		 clock_timestamp(), 'M');
end if;

CALL pls_grava_historico_notific(nr_seq_notificacao_w,ds_historico_w,null,null,nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
