CREATE OR REPLACE PROCEDURE pls_solic_rescisao_financeira ( nr_seq_solic_resc_fin_p pls_solic_rescisao_fin.nr_sequencia%type, nr_seq_mensalidade_p pls_solic_resc_fin_item.nr_seq_mensalidade%type, ie_opcao_p text, nr_seq_pagador_p pls_contrato_pagador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/*
ie_opcao_p
'GD'	Gerar devolução
'EM'	Excluir mensalidade
'GN'	Gerar negociação
*/
BEGIN

if (ie_opcao_p = 'GD') then
	CALL pls_solic_rescisao_fin_pck.gerar_devolucao(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p);
elsif (ie_opcao_p = 'EM') then
	CALL pls_solic_rescisao_fin_pck.remover_tit_rescisao_fin(nr_seq_mensalidade_p, nr_seq_solic_resc_fin_p, nm_usuario_p);
elsif (ie_opcao_p = 'GN') then
	CALL pls_solic_rescisao_fin_pck.gerar_negociacao(nr_seq_solic_resc_fin_p, nr_seq_pagador_p, nm_usuario_p, cd_estabelecimento_p);
end if;

end;
