CREATE OR REPLACE PROCEDURE pls_solic_resc_analise_fin ( nr_seq_solic_resc_fin_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_pagador_w	pls_contrato_pagador.nr_sequencia%type;
nr_seq_solicitacao_w	pls_solicitacao_rescisao.nr_sequencia%type;
				

BEGIN

if (ie_opcao_p = 'I') then
	CALL pls_solic_rescisao_fin_pck.iniciar_analise(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p);
elsif (ie_opcao_p = 'D') then
	CALL pls_solic_rescisao_fin_pck.desfazer_analise(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p);
elsif (ie_opcao_p = 'F') then
	CALL pls_solic_rescisao_fin_pck.finalizar_analise(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p);
elsif (ie_opcao_p = 'N') then
	select	max(nr_seq_pagador),
		max(nr_seq_solicitacao)
	into STRICT	nr_seq_pagador_w,
		nr_seq_solicitacao_w
	from	pls_solic_rescisao_fin
	where	nr_sequencia = nr_seq_solic_resc_fin_p;
	
	CALL pls_solic_rescisao_fin_pck.gerar_nova_analise(nr_seq_solic_resc_fin_p, nr_seq_solicitacao_w, nr_seq_pagador_w, nm_usuario_p);
elsif (ie_opcao_p = 'C') then
	CALL pls_solic_rescisao_fin_pck.cancelar_analise_fin(nr_seq_solic_resc_fin_p, nm_usuario_p, cd_estabelecimento_p,'S');
end if;

commit;

end;
$body$
