CREATE OR REPLACE PROCEDURE pls_solic_transferencia_titu ( nr_seq_solicitacao_p bigint, ie_opcao_p text, ds_observacao_rejeicao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Rejeita ou aprova a solicitação de transferência de titularidade
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
'R' - Rejeitar
'A' - Aprovar

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (ie_opcao_p = 'R') then
	update	pls_solic_transf_titular
	set	dt_rejeicao		= clock_timestamp(),
		ie_status		= 2,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		ds_observacao_rejeicao  = ds_observacao_rejeicao_p
	where	nr_sequencia	= nr_seq_solicitacao_p;
elsif (ie_opcao_p = 'A') then
	update	pls_solic_transf_titular
	set	dt_confirmacao		= clock_timestamp(),
		ie_status		= 1,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia	= nr_seq_solicitacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
