CREATE OR REPLACE PROCEDURE pls_suspend_atend_contrato ( nr_seq_suspensao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_contrato_w	bigint;
nr_seq_segurado_w	bigint;
nr_seq_motivo_susp_w	pls_segurado_suspensao.nr_seq_motivo_susp%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_w
	and	(((dt_rescisao IS NOT NULL AND dt_rescisao::text <> '') and dt_rescisao >= clock_timestamp()) or (coalesce(dt_rescisao::text, '') = ''));


BEGIN

select	nr_seq_contrato,
	nr_seq_motivo_susp
into STRICT	nr_seq_contrato_w,
	nr_seq_motivo_susp_w
from	pls_segurado_suspensao
where	nr_sequencia	= nr_seq_suspensao_p;

update	pls_segurado_suspensao
set	dt_inicio_suspensao	= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_suspensao_p;

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_segurado
	set	ie_situacao_atend	= 'S',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_seq_suspensao	= nr_seq_suspensao_p,
		nr_seq_motivo_susp	= nr_seq_motivo_susp_w
	where	nr_sequencia		= nr_seq_segurado_w;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '58', clock_timestamp(), 'Suspensão de atendimento através do contrato',
					'pls_suspend_atend_contrato', null, null, null,
					null, null, null, null,
					null, null, null, null,
					nm_usuario_p, 'N');

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
