SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_tiss_consistir_protocolo ( nr_sequencia_p bigint, ie_tipo_glosa_p text, ie_evento_p text, nr_seq_prestador_p bigint, nr_seq_ocorrencia_p bigint, ds_parametro_um_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registro_w			bigint;
nr_seq_protocolo_w		bigint;
qt_contas_informadas_w		bigint;
ie_situacao_w			varchar(2);
ie_qtd_diferente_w		varchar(1) := 'N';

BEGIN


/* Obter dados do protocolo*/

	select	max(prot.qt_contas_informadas),
		max(prot.ie_situacao),
		max(conta.nr_seq_protocolo)
	into STRICT	qt_contas_informadas_w,
		ie_situacao_w,
		nr_seq_protocolo_w
	from	pls_protocolo_conta	prot,
		pls_conta		conta
	where	prot.nr_sequencia 	= conta.nr_seq_protocolo
	and	conta.nr_sequencia	= nr_sequencia_p;


if (qt_contas_informadas_w IS NOT NULL AND qt_contas_informadas_w::text <> '') and (ie_situacao_w = 'T') then
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_w;

	if (qt_registro_w <> qt_contas_informadas_w) then
		ie_qtd_diferente_w := 'S';
	end if;
end if;

/* 1101  Quantidade de Guias informadas no Protocolo diferente das cadastradas*/

if (ie_qtd_diferente_w = 'S') then
	CALL pls_gravar_glosa_tiss('1101',
		null, ie_evento_p,
		nr_sequencia_p, ie_tipo_glosa_p, nr_seq_prestador_p,
		nr_seq_ocorrencia_p, '', nm_usuario_p,
		cd_estabelecimento_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
