CREATE OR REPLACE PROCEDURE pls_tiss_consistir_taxa ( nr_sequencia_p bigint, ie_tipo_glosa_p text, ie_evento_p text, nr_seq_prestador_p bigint, nr_seq_ocorrencia_p bigint, ds_parametro_um_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_origem_proced_p bigint) AS $body$
DECLARE


/* IE_TIPO_GLOSA_P
	C - Conta
	CP - Conta procedimento
	CM - Conta material
	A - Autorização
	AP - Autorização procedimento
	AM - Autorização material
*/
cd_procedimento_w		bigint;
ie_origem_proced_w		integer;
qt_proc_w			smallint;
nr_seq_conta_w			pls_conta.nr_sequencia%type;


BEGIN

if (ie_tipo_glosa_p	= 'CP') then
	begin
		select	coalesce(cd_procedimento,cd_procedimento_imp),
			coalesce(ie_origem_proced,ie_origem_proced_p),
			nr_seq_conta
		into STRICT	cd_procedimento_w,
			ie_origem_proced_w,
			nr_seq_conta_w
		from	pls_conta_proc
		where	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		cd_procedimento_w := null;
		ie_origem_proced_w := null;
	end;

	select	count(1)
	into STRICT	qt_proc_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;
end if;


/* 2401 - Taxa / Aluguel inválido*/

if (qt_proc_w = 0) then
	CALL pls_gravar_glosa_tiss('2401',
		null, ie_evento_p,
		nr_sequencia_p, ie_tipo_glosa_p, nr_seq_prestador_p,
		nr_seq_ocorrencia_p, '', nm_usuario_p,
		cd_estabelecimento_p, nr_seq_conta_w);
end if;
/*
commit;
*/
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
