CREATE OR REPLACE PROCEDURE pls_todos_itens_regra_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_regra_p pls_regra_lib_analise.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, ie_regra_valida_p INOUT text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Veriricar todos itens das regras para liberação de análise
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_item_w		bigint;
ds_retorno_w		varchar(1);

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_material
	from	pls_criterio_lib_analise
	where	nr_seq_regra = nr_seq_regra_p;

BEGIN
ds_retorno_w := 'S';

for r_C01_w in C01 loop

	begin
	if (r_C01_w.cd_procedimento IS NOT NULL AND r_C01_w.cd_procedimento::text <> '') then
		select	count(1)
		into STRICT	qt_item_w
		from	pls_conta_proc_v
		where	cd_procedimento 	= r_C01_w.cd_procedimento
		and	ie_origem_proced 	= r_C01_w.ie_origem_proced
		and	nr_seq_analise 		= nr_seq_analise_p;

			if (coalesce(qt_item_w,0) = 0) then
				ds_retorno_w 	:= 'N';
				exit;
			end if;
	end if;
	if (r_C01_w.nr_seq_material IS NOT NULL AND r_C01_w.nr_seq_material::text <> '') then
		select	count(1)
		into STRICT	qt_item_w
		from	pls_conta_mat_v
		where	nr_seq_material 	= r_C01_w.nr_seq_material
		and	nr_seq_analise 		= nr_seq_analise_p;

			if (coalesce(qt_item_w,0) = 0) then
				ds_retorno_w 	:= 'N';
				exit;
			end if;
	end if;
	end;
end loop;

ie_regra_valida_p := ds_retorno_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
