CREATE OR REPLACE PROCEDURE pls_tot_monitor_conferencia ( nr_seq_lote_p pls_lot_monit_tiss_confer.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Sumariza os indicadores no arquivo, guardados na tabela filha, e atualiza o lote

-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_total_cobranca_guia_w	pls_lot_monit_tiss_confer.vl_total_cobranca_guia%type;
vl_total_glosa_w		pls_lot_monit_tiss_confer.vl_total_glosa%type;
vl_total_pago_w			pls_lot_monit_tiss_confer.vl_total_pago%type;
vl_total_fornecedor_w		pls_lot_monit_tiss_confer.vl_total_fornecedor%type;
vl_total_tabela_propria_w	pls_lot_monit_tiss_confer.vl_total_tabela_propria%type;
vl_total_copart_w		pls_lot_monit_tiss_confer.vl_total_copart%type;


BEGIN

select	sum(a.vl_cobranca_guia),
	sum(a.vl_total_glosa),
	sum(a.vl_total_pago),
	sum(a.vl_total_fornecedor),
	sum(a.vl_total_tabela_propria),
	sum(a.vl_total_copart)
into STRICT	vl_total_cobranca_guia_w,
	vl_total_glosa_w,
	vl_total_pago_w,
	vl_total_fornecedor_w,
	vl_total_tabela_propria_w,
	vl_total_copart_w
from	pls_monit_tiss_confer	a
where	a.nr_seq_lote		= nr_seq_lote_p;


update	pls_lot_monit_tiss_confer	a
set	a.vl_total_cobranca_guia	= vl_total_cobranca_guia_w,
	a.vl_total_glosa		= vl_total_glosa_w,
	a.vl_total_pago			= vl_total_pago_w,
	a.vl_total_fornecedor		= vl_total_fornecedor_w,
	a.vl_total_tabela_propria	= vl_total_tabela_propria_w,
	a.vl_total_copart		= vl_total_copart_w,
	dt_atualizacao			= clock_timestamp(),
	nm_usuario			= nm_usuario_p
where	a.nr_sequencia			= nr_seq_lote_p;


if (coalesce(ie_commit_p, 'N') = 'S') then

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
