SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_tps_obter_desconto ( nr_seq_tps_p bigint, nr_seq_regra_p INOUT bigint, pr_desconto_p INOUT bigint) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
qt_benef_ativo_w		bigint	:= 0;
nr_seq_regra_w			bigint;
pr_desconto_w			double precision	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		pr_desconto
	from	pls_tps_desconto
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	qt_benef_ativo_w between qt_vidas_minima and qt_vidas_maxima;


BEGIN

/* Obter dados da TPS */

begin
select	cd_estabelecimento,
	dt_periodo_inicial,
	dt_periodo_final
into STRICT	cd_estabelecimento_w,
	dt_periodo_inicial_w,
	dt_periodo_final_w
from	pls_tps
where	nr_sequencia	= nr_seq_tps_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267459,
						'NR_SEQ_TPS_P=' || nr_seq_tps_p);
end;

/* Obter a quantidade de beneficiário ativos no final do período da TPS */

select	count(*)
into STRICT	qt_benef_ativo_w
from	pls_segurado
where	last_day(dt_periodo_final_w) between trunc(dt_contratacao,'month') and trunc(coalesce(dt_rescisao,clock_timestamp()),'month');

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	pr_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_regra_w	:= nr_seq_regra_w;
	pr_desconto_w	:= pr_desconto_w;
	end;
end loop;
close C01;

nr_seq_regra_p	:= nr_seq_regra_w;
pr_desconto_p	:= pr_desconto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
