SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_transf_contas_compl_web ( nr_seq_protocolo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE



nr_seq_conta_w		bigint;
nr_seq_protocolo_w	bigint;
nr_seq_prestador_w	bigint;
qt_conta_protocolo_w	bigint;
ie_tipo_guia_w		smallint;
dt_mes_competencia_w	timestamp;
dt_lib_complemento_w	timestamp;
ie_vinculo_complemento_w	pls_web_param_geral.ie_vinculo_complemento%type;
qt_prot_medico_w	integer;		
C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_lib_complemento,
		cd_medico_solicitante
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p
	and	ie_status != 'C'
	and	coalesce(dt_lib_complemento::text, '') = '';	
	
BEGIN

select	max(ie_vinculo_complemento)
into STRICT	ie_vinculo_complemento_w
from	pls_web_param_geral
where	cd_estabelecimento	= cd_estabelecimento_p;

/* Verifica um protocolo para poder transferir as contas */

begin
	select	nr_seq_prestador,
		ie_tipo_guia
	into STRICT	nr_seq_prestador_w,
		ie_tipo_guia_w
	from 	pls_protocolo_conta
	where	nr_sequencia = nr_seq_protocolo_p;
exception
	when others then
	nr_seq_prestador_w := null;
end;

if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then
	/* Verifica as contas pendentes de complemento e transfere para o novo protocolo*/

	for r_c01_w in C01() loop
		begin
		select 	coalesce(max(nr_sequencia), 0)
			into STRICT	nr_seq_protocolo_w
			from 	pls_protocolo_conta	a
			where	ie_protocolo_compl 	= 'S'
			and	ie_status		= 1
			and	ie_tipo_guia		= ie_tipo_guia_w
			and	nr_seq_prestador	= nr_seq_prestador_w
			and	ie_situacao 		= 'I'
			and	nr_sequencia  		<> nr_seq_protocolo_p
			and (SELECT count(1)
				 from 	pls_conta  
				 where 	nr_seq_protocolo = a.nr_sequencia 
				 and	ie_status <> 'C') < 100;

			if (nr_seq_protocolo_w = 0) then
				select	nextval('pls_protocolo_conta_seq')
				into STRICT	nr_seq_protocolo_w
				;

				dt_mes_competencia_w	:= pls_obter_dataref_prot_imp(	nr_seq_prestador_w, 'C',clock_timestamp(),
											clock_timestamp(), clock_timestamp(), clock_timestamp(),
											ie_tipo_guia_w,null, cd_estabelecimento_p);
				
				insert into pls_protocolo_conta(nr_sequencia, dt_atualizacao, nm_usuario,
								ie_status, ie_situacao, cd_estabelecimento,             
								dt_mes_competencia, nr_seq_prestador, ie_apresentacao, 
								ie_protocolo_compl, ie_tipo_guia, ie_origem_protocolo,
								ie_tipo_protocolo)
							values (	nr_seq_protocolo_w, clock_timestamp(), nm_usuario_p,  
								'1', 'I', cd_estabelecimento_p,
								dt_mes_competencia_w, nr_seq_prestador_w, 'A', 
								'S', ie_tipo_guia_w,'C',
								'C');
			end if;

		if (ie_vinculo_complemento_w = 'PM' ) then
			select	count(1)
			into STRICT	qt_prot_medico_w
			from	pls_protocolo_conta p
			where	nr_sequencia	= nr_seq_protocolo_w
			and	not exists (SELECT 1
						from	pls_conta	c
						where	c.nr_seq_protocolo = p.nr_sequencia
						and	c.cd_medico_solicitante != r_c01_w.cd_medico_solicitante);
						
			if (qt_prot_medico_w	= 0) then
				update	pls_conta
				set	nr_seq_protocolo = nr_seq_protocolo_w
				where   nr_sequencia 	= r_c01_w.nr_sequencia;
			else
				select 	coalesce(min(nr_sequencia), 0)
				into STRICT	nr_seq_protocolo_w
				from 	pls_protocolo_conta a
				where	ie_protocolo_compl 	= 'S'
				and	ie_status		= 1
				and	ie_tipo_guia		= ie_tipo_guia_w
				and	nr_seq_prestador	= nr_seq_prestador_w
				and	ie_situacao 		= 'I'
				and (SELECT count(1)
					 from 	pls_conta  
					 where 	nr_seq_protocolo = a.nr_sequencia 
					 and	ie_status <> 'C') < 100
				and	exists (select 1
						from 	pls_conta  	x
						where 	x.nr_seq_protocolo 	= a.nr_sequencia 
						and	x.cd_medico_solicitante	= r_c01_w.cd_medico_solicitante);

				if (nr_seq_protocolo_w = 0) then
					select	nextval('pls_protocolo_conta_seq')
					into STRICT	nr_seq_protocolo_w
					;

					dt_mes_competencia_w	:= pls_obter_dataref_prot_imp(	nr_seq_prestador_w, 'C',clock_timestamp(),
												clock_timestamp(), clock_timestamp(), clock_timestamp(), 
												ie_tipo_guia_w,null, cd_estabelecimento_p);
					
					insert into pls_protocolo_conta(nr_sequencia, dt_atualizacao, nm_usuario,
									ie_status, ie_situacao, cd_estabelecimento,             
									dt_mes_competencia, nr_seq_prestador, ie_apresentacao, 
									ie_protocolo_compl, ie_tipo_guia, ie_origem_protocolo,
									ie_tipo_protocolo)
								values (	nr_seq_protocolo_w, clock_timestamp(), nm_usuario_p,  
									'1', 'I', cd_estabelecimento_p,
									dt_mes_competencia_w, nr_seq_prestador_w, 'A', 
									'S', ie_tipo_guia_w,'C',
									'C');
				end if;
				
				update	pls_conta
				set	nr_seq_protocolo = nr_seq_protocolo_w
				where   nr_sequencia 	= r_c01_w.nr_sequencia;
			end if;
		else
			update	pls_conta
			set	nr_seq_protocolo = nr_seq_protocolo_w
			where   nr_sequencia 	= r_c01_w.nr_sequencia;
		end if;
		end;
	end loop;
	
	
	select	count(1)
	into STRICT	qt_conta_protocolo_w
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_w;

	if (qt_conta_protocolo_w = 0) then
		update	pls_protocolo_conta
		set	ie_status    = '4',
			ie_situacao  = 'D',
			ds_observacao = 'Protocolo cancelado pois não possui contas devido a transferência de contas do complemento do portal.'
		where	nr_sequencia = nr_seq_protocolo_w;
	end if;	
	select	count(1)
	into STRICT	qt_conta_protocolo_w
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p;

	if (qt_conta_protocolo_w = 0) then
		update	pls_protocolo_conta
		set	ie_status    = '4',
			ie_situacao  = 'D',
			ds_observacao = 'Protocolo cancelado pois não possui contas devido a transferência de contas do complemento do portal.'
		where	nr_sequencia = nr_seq_protocolo_p;
	end if;
end if;	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_transf_contas_compl_web ( nr_seq_protocolo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

