SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_transf_contas_web ( nr_seq_protocolo_p bigint, qt_max_contas_p bigint, cd_estabelecimento_p bigint, nr_seq_usu_prestador_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_protocolo_w		bigint;
nr_seq_protocolo_ww		bigint;
nr_seq_conta_w			bigint;
nr_seq_procedimento_w		bigint;
nr_seq_material_w		bigint;
nr_seq_prestador_w		bigint;
ie_tipo_guia_w			varchar(2);
ie_valida_prot_qtd_w		varchar(1);
qtd_glosas_conta_w		integer := 0;
nr_seq_protocolo_ref_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		b.nr_seq_prestador, 
		b.ie_tipo_guia, 
		b.nr_seq_prot_referencia 
	from	pls_conta a, 
		pls_protocolo_conta b 
	where	a.nr_seq_protocolo = nr_seq_protocolo_w 
	and	a.nr_seq_protocolo = b.nr_sequencia;
	

BEGIN 
 
nr_seq_protocolo_w := nr_seq_protocolo_p;
 
open C01;
loop 
fetch C01 into 
	nr_seq_conta_w, 
	nr_seq_prestador_w, 
	ie_tipo_guia_w, 
	nr_seq_protocolo_ref_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(1) 
	into STRICT 	qtd_glosas_conta_w 
	from	pls_conta_glosa_ocorrencia_v 
	where	nr_seq_conta = nr_seq_conta_w;
	 
	/* Verifica se existe glosa para os procedimetnos da conta , caso ainda não tenha encontrado glosa para a conta */
 
	if (qtd_glosas_conta_w = 0) then 
		 
		select	count(1) 
		into STRICT 	qtd_glosas_conta_w 
		from	pls_conta_proc a, 
			pls_conta_glosa_ocorrencia_v b 
		where	b.nr_seq_proc = a.nr_sequencia 
		and	a.nr_seq_conta = nr_seq_conta_w;
		 
		/* Verifica se existe glosa para os materiais da conta , caso ainda não tenha encontrado glosa nos procedimentos */
 
		if (qtd_glosas_conta_w = 0) then 
			select	count(1) 
			into STRICT 	qtd_glosas_conta_w 
			from	pls_conta_mat a, 
				pls_conta_glosa_ocorrencia_v b 
			where	b.nr_seq_mat = a.nr_sequencia 
			and	a.nr_seq_conta = nr_seq_conta_w;
		end if;	
	end if;
	 
	if ( qtd_glosas_conta_w > 0 ) then 
	 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_protocolo_ww 
		from 	pls_protocolo_conta 
		where	ie_tipo_guia = ie_tipo_guia_w 
		and 	ie_situacao = 'I' 
		and	ie_status = '1' 
		and	ie_apresentacao = 'A' 
		and	ie_tipo_protocolo = 'C' 
		and 	ie_forma_imp = 'D' 
		and	nr_seq_prestador = nr_seq_prestador_w 
		and	coalesce(nr_seq_prot_referencia,0) = coalesce(nr_seq_protocolo_ref_w,0) 
		and	nr_sequencia <> nr_seq_protocolo_p;
		 
		nr_seq_protocolo_w := nr_seq_protocolo_ww;
		 
		/* Verificação de quantidade de contas por protocolos */
 
		if (qt_max_contas_p <> 0) then 
			ie_valida_prot_qtd_w := pls_valida_qtd_conta_web(qt_max_contas_p, ie_tipo_guia_w, nr_seq_prestador_w ,nr_seq_protocolo_w);
			if (ie_valida_prot_qtd_w = 'N') then 
				nr_seq_protocolo_w := null;
			end if;
		end if;
		 
		if (coalesce(nr_seq_protocolo_w::text, '') = '') then 
			nr_seq_protocolo_w := pls_gerar_protocolo_web(nr_seq_prestador_w, null, ie_tipo_guia_w, qt_max_contas_p, cd_estabelecimento_p, nm_usuario_p, 'N', nr_seq_protocolo_ref_w, nr_seq_usu_prestador_p, nr_seq_protocolo_w);
		end if;
		 
		-- Atualizar a conta com o novo protocolo 
		update	pls_conta 
		set	nr_seq_protocolo = nr_seq_protocolo_w 
		where	nr_sequencia = nr_seq_conta_w;
		 
		-- OS 1286795 - jtonon - Atualizar a conta medica resumo com o novo protocolo. 
		-- Realizamos este tratamento visto que a trigger pls_conta_log registrou troca de protocolo no update acima, porém, na conta médica resumo ficou registrado o protocolo antigo, neste caso estamos atuaizando a conta médica resumo com o novo protocolo também. 
		update	pls_conta_medica_resumo 
		set	nr_seq_protocolo 	= nr_seq_protocolo_w 
		where	nr_seq_protocolo 	= nr_seq_protocolo_p 
		and	nr_seq_conta		= nr_seq_conta_w;
	end if;
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_transf_contas_web ( nr_seq_protocolo_p bigint, qt_max_contas_p bigint, cd_estabelecimento_p bigint, nr_seq_usu_prestador_p bigint, nm_usuario_p text) FROM PUBLIC;

