SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_tratar_analise_consistida (nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Tratar a geração do fluxo de auditoria das análises, mudança de status,
fechamento
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta a
	where	a.nr_seq_analise = nr_seq_analise_p
	and	a.ie_status not in ('F','C')
	Order by a.vl_cobrado Desc;
	
nr_seq_grupo_pre_analise_w	bigint;
ie_gerar_analise_audit_w	varchar(3);
ie_liberar_w			varchar(3);
qt_ocor_pre_w			bigint;
ie_status_w			varchar(10);
nr_seq_grupo_criado_w		bigint;
nr_seq_conta_w			bigint;
nr_seq_fatura_w			bigint;
ds_erro_w			varchar(4000);
vetor_conta_w			pls_util_cta_pck.num_table_vazia_w%type;
qt_reg_w			integer;
qt_reg_audit_w			integer;

BEGIN

if (coalesce(nr_seq_analise_p,0) > 0) then
	
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END ,
		max(a.nr_seq_fatura)
	into STRICT	ie_gerar_analise_audit_w,
		nr_seq_fatura_w
	from	pls_ocorrencia c,
		pls_ocorrencia_benef b,
		pls_conta a
	where	a.nr_sequencia	= b.nr_seq_conta
	and	c.nr_sequencia	= b.nr_seq_ocorrencia
	and	a.nr_seq_analise = nr_seq_analise_p
	-- deve considerar apenas os ativos OU as ocorrencias meramente informativas
	and	((b.ie_situacao = 'A') or (c.ie_auditoria_conta = 'N'));

	select	CASE WHEN ie_gerar_analise_audit_w='S' THEN  'N'  ELSE 'S' END
	into STRICT	ie_liberar_w
	;

	select	max(a.ie_status)
	into STRICT	ie_status_w
	from	pls_analise_conta	a
	where	a.nr_sequencia	= nr_seq_analise_p;
	
	-- se já está sendo auditada não faz nada com os fluxos
	select	count(1)
	into STRICT	qt_reg_audit_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_analise = nr_seq_analise_p
	and	(nm_auditor_atual IS NOT NULL AND nm_auditor_atual::text <> '');

	if (ie_gerar_analise_audit_w = 'S') and /*Diego OS 307952 - 08/04/2011 -  Só ficarão com estágio "Em análise" quando houver ocorrências que devem gerar análise*/
		(ie_status_w not in ('T','L','X')) and (qt_reg_audit_w = 0) then /* Tratato para se já estiver como liberado para fechamento não permitir voltar o status */
		
		CALL pls_alterar_status_analise_cta(nr_seq_analise_p, 'G', 'PLS_CTA_PROCESSO_PCK.EXECUTA_PROCESSO', nm_usuario_p, cd_estabelecimento_p); /*A análise é setada como sendo aguardando análise*/
		
		update	pls_analise_conta
		set	ie_auditoria 	= 'S'
	 	where	nr_sequencia 	= nr_seq_analise_p;
		
		qt_reg_w := 0;
		vetor_conta_w := pls_util_cta_pck.num_table_vazia_w;
		open c01;
		loop
		fetch c01 into	
			nr_seq_conta_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (ie_gerar_analise_audit_w = 'S') then /*Gerar grupo da analise*/
				CALL pls_gerar_grupo_aud_conta(nr_seq_conta_w, nr_seq_analise_p, nm_usuario_p, cd_estabelecimento_p);
			end if;
			
			CALL pls_gerar_fluxo_audit_item(nr_seq_analise_p,nr_seq_conta_w,null,null,null,nm_usuario_p);
			/*A conta é setada para ficar em análise*/

			vetor_conta_w(qt_reg_w) := nr_seq_conta_w;
			qt_reg_w := qt_reg_w + 1;
			end;
		end loop;
		close c01;
		
		forall i in vetor_conta_w.first .. vetor_conta_w.last
			update	pls_conta
			set	ie_status 	= 'A'
			where	nr_sequencia 	= vetor_conta_w(i);
		
		
	elsif (ie_status_w <> 'X') then
		for r_c01_w in C01() loop
			begin
			CALL pls_fechar_conta(r_c01_w.nr_sequencia, 'N', ie_liberar_w, 'N', cd_estabelecimento_p, nm_usuario_p, 'S', null);
			end;
		end loop;
		ds_erro_w := pls_finalizar_analise_grupo(	nr_seq_analise_p, nm_usuario_p, null, 'S', 'S', cd_estabelecimento_p, 'N', 'S', 'S', 'L', ds_erro_w);
	end if;

	/* Se foram geradas ocorrências de pré-análise, é necessário ter grupo de pré-análise definido */

	if (nr_seq_fatura_w IS NOT NULL AND nr_seq_fatura_w::text <> '') then
		select	count(1)
		into STRICT	qt_ocor_pre_w
		from	pls_ocorrencia_benef b,
			pls_conta a
		where	a.nr_sequencia		= b.nr_seq_conta
		and	a.nr_seq_analise	= nr_seq_analise_p
		and	b.ie_pre_analise	= 'S';
		
		if (qt_ocor_pre_w > 0) then
			begin
			select	a.nr_seq_grupo_pre_analise
			into STRICT	nr_seq_grupo_pre_analise_w
			from	pls_parametros a
			where	a.cd_estabelecimento	= cd_estabelecimento_p;
			exception
			when others then
				nr_seq_grupo_pre_analise_w	:= null;
			end;
			
			if (coalesce(nr_seq_grupo_pre_analise_w::text, '') = '') then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(210448);
			else
				nr_seq_grupo_criado_w := pls_inserir_grupo_aud(	nr_seq_analise_p, nr_seq_grupo_pre_analise_w, 0, 'Gerado pelo sistema devido a pré-análise de arquivo PTU', nm_usuario_p, cd_estabelecimento_p, nr_seq_grupo_criado_w);
						
				update	pls_auditoria_conta_grupo
				set	ie_pre_analise	= 'S',
					dt_atualizacao	= clock_timestamp(),
					nm_usuario	= nm_usuario_p
				where	nr_sequencia	= nr_seq_grupo_criado_w;
			end if;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_tratar_analise_consistida (nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

