SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_tratar_material_brasindice (dt_importacao_p timestamp, ie_atualiza_pls_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_material_w	bigint;
qt_registro_w		bigint;
cd_tiss_w		varchar(15);
dt_importacao_w		timestamp;
ie_atualiza_dt_exc_w	varchar(5) := 'N';

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_tiss_brasindice
	from	pls_material a
	where	(a.cd_tiss_brasindice IS NOT NULL AND a.cd_tiss_brasindice::text <> '')
	and	coalesce(a.dt_exclusao::text, '') = ''
	and	a.cd_estabelecimento	= cd_estabelecimento_p;


BEGIN
if (ie_atualiza_pls_p  ('S','SE')) then
	ie_atualiza_dt_exc_w := 'S'; -- Atualizar a data de exclus√£o
end if;

dt_importacao_w	:= trunc(coalesce(dt_importacao_p,clock_timestamp()),'dd');

open c01;
loop
fetch c01 into
	nr_seq_material_w,
	cd_tiss_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	brasindice_preco a
	where	a.cd_tiss	= cd_tiss_w
	and	trunc(a.dt_inicio_vigencia,'dd') = dt_importacao_w;

	if (qt_registro_w = 0) then
		update	pls_material
		set	dt_exclusao	= CASE WHEN  ie_atualiza_dt_exc_w='S' THEN  coalesce(dt_importacao_p,clock_timestamp())  ELSE dt_exclusao END ,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= 'BRASINDICE'
		where	nr_sequencia	= nr_seq_material_w;
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
