SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_tratar_regra_dt_conta ( ie_data_conta_p bigint, dt_emissao_p timestamp, dt_alta_int_p timestamp, dt_entrada_int_p timestamp, dt_protocolo_p timestamp, nm_usuario_p text, ie_gera_ocorrencia_p INOUT text) AS $body$
BEGIN
/*Consistência da data da conta*/

/*Data da alta menor do que a data da entrada*/

if (ie_data_conta_p = 1) then
	/*Se a data da alta for maior do que a data da entrada a regra não é válida*/

	if ( dt_alta_int_p >= dt_entrada_int_p ) then --verificar se é valido somente para internação
		ie_gera_ocorrencia_p	:='N';
	end if;

/*Data da alta maior do que a data atual*/

elsif (ie_data_conta_p = 2) then
	/*Se a data da alta for menor do que a data atual a regra não é válida*/

	if	((trunc(dt_alta_int_p,'dd')) <= trunc(clock_timestamp(),'dd')) then
		ie_gera_ocorrencia_p	:='N';
	end if;

/*Data de emissão da conta maior do que a data atual*/

elsif (ie_data_conta_p = 3) then
	/*Se a data de emissão da conta for menor do que a data atual a regra não será válida*/

	if (trunc(dt_emissao_p,'dd') <= trunc(clock_timestamp(),'dd')) then
		ie_gera_ocorrencia_p	:='N';
	end if;

/*Data de entrada maior do que data atual*/

elsif (ie_data_conta_p = 4) then
	/*Se a data de entrada for menor do que data atual a regra não é válida*/

	if (trunc(dt_entrada_int_p,'dd') <= trunc(clock_timestamp(),'dd')) then
		ie_gera_ocorrencia_p	:='N';
	end if;
/*Data de emissão da conta maior que a data do protocolo*/

elsif (ie_data_conta_p = 5)	then
	if (dt_emissao_p IS NOT NULL AND dt_emissao_p::text <> '')	and (dt_protocolo_p IS NOT NULL AND dt_protocolo_p::text <> '')	then
		if (dt_emissao_p <= dt_protocolo_p)	then
			ie_gera_ocorrencia_p := 'N';
		end if;
	else
		ie_gera_ocorrencia_p	:= 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
