CREATE OR REPLACE PROCEDURE pls_tratar_w_analise_item_exp ( nr_seq_analise_p bigint, nr_seq_conta_proc_p bigint, ie_tipo_guia_p text, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint	:= 0;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir ou limpar a tabela temporária para controle de expansão dos itens da análise
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	IE_ACAO_P
		E - Expandir
		O - Ocultar
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_conta_proc_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta_proc	a
	where	nr_seq_agrup_analise	= nr_seq_conta_proc_p
	and	nr_sequencia		<> nr_seq_conta_proc_p;


BEGIN
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') or
	((ie_tipo_guia_p IS NOT NULL AND ie_tipo_guia_p::text <> '') and (coalesce(nr_seq_conta_proc_p::text, '') = '')) then
	if (ie_acao_p = 'E') then
		nr_sequencia_w	:= nr_sequencia_w + 1;

		insert into w_pls_analise_item_exp(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nr_seq_analise,
			nr_seq_conta_proc,
			ie_tipo_guia)
		values (nr_sequencia_w,
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_analise_p,
			nr_seq_conta_proc_p,
			ie_tipo_guia_p);

		open C01;
		loop
		fetch C01 into
			nr_seq_conta_proc_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			CALL pls_tratar_w_analise_item_exp(nr_seq_analise_p, nr_seq_conta_proc_w, null, ie_acao_p, nm_usuario_p);
			end;
		end loop;
		close C01;
	elsif (ie_acao_p = 'O') then
		if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
			delete	from w_pls_analise_item_exp
			where	nr_seq_analise		= nr_seq_analise_p
			and	nr_seq_conta_proc	= nr_seq_conta_proc_p;
		else
			delete	from w_pls_analise_item_exp
			where	nr_seq_analise		= nr_seq_analise_p
			and	coalesce(nr_seq_conta_proc::text, '') = ''
			and	ie_tipo_guia		= ie_tipo_guia_p;
		end if;

		open C01;
		loop
		fetch C01 into
			nr_seq_conta_proc_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			CALL pls_tratar_w_analise_item_exp(nr_seq_analise_p, nr_seq_conta_proc_w, null, ie_acao_p, nm_usuario_p);
			end;
		end loop;
		close C01;
	end if;
else
	if (ie_acao_p = 'O') then
		delete	from w_pls_analise_item_exp
		where	nr_seq_analise		= nr_seq_analise_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
