CREATE OR REPLACE PROCEDURE pls_update_anexo_req_item_web ( nr_seq_anexo_p bigint, ie_tipo_item_p text, nr_registro_anvisa_p text, cd_ref_fabricante_imp_p text, dt_prevista_p timestamp, ie_frequencia_dose_p bigint, ie_via_administracao_p text, ie_opcao_fabricante_p text, cd_aut_funcionamento_p text, nm_usuario_p text, qt_unidade_medida_p text, qt_dosagem_total_p bigint) AS $body$
BEGIN


if (ie_tipo_item_p = 'M') then

	update	pls_lote_anexo_mat_aut
	set	nr_registro_anvisa 	= nr_registro_anvisa_p,
		cd_ref_fabricante_imp 	= cd_ref_fabricante_imp_p,
		dt_prevista 		= dt_prevista_p,
		ie_frequencia_dose 	= ie_frequencia_dose_p,
		ie_via_administracao 	= ie_via_administracao_p,
		ie_opcao_fabricante	= ie_opcao_fabricante_p,
		cd_aut_funcionamento 	= cd_aut_funcionamento_p,
		qt_unidade_medida	= qt_unidade_medida_p,
		qt_dosagem_total	= qt_dosagem_total_p
	where	nr_sequencia 		= nr_seq_anexo_p;

else
	update	pls_lote_anexo_proc_aut
	set	dt_prev_realizacao 	= dt_prevista_p
	where	nr_sequencia		= nr_seq_anexo_p;

end if;

commit;

end;
$body$
