CREATE OR REPLACE PROCEDURE pls_upd_vlpend_cta_ressarc ( nr_seq_conta_p pls_formulario.nr_seq_conta%type, vl_pedido_p pls_formulario.vl_pedido%type, vl_conta_p pls_formulario.vl_pedido%type, nm_usuairo_p text) AS $body$
DECLARE


vl_pedido_w	pls_formulario.vl_pedido%type;
vl_conta_w	pls_formulario.vl_pedido%type;


BEGIN

if (nr_seq_conta_p > 0) then

	vl_pedido_w	:= coalesce(vl_pedido_p, 0);
	vl_conta_w	:= coalesce(vl_conta_p, 0);

	if (vl_pedido_w = 0) then
		vl_pedido_w := null;
	end if;

	vl_conta_w := vl_conta_w - coalesce(vl_pedido_w,0);

	if (vl_conta_w = 0) then
		vl_conta_w := null;
	end if;

	update	pls_processo_conta
	set	vl_pendente 	= vl_conta_w,
		vl_ressarcir	= vl_pedido_w,
		nm_usuario	= nm_usuairo_p,
		dt_atualizacao 	= clock_timestamp()
	where	nr_sequencia 	= nr_seq_conta_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
