CREATE OR REPLACE PROCEDURE pls_usuario_centro_lanc_progr ( nr_seq_lancamento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
cd_setor_atendimento_w		bigint;
cd_centro_custo_w		bigint;
ie_lanc_centro_custo_w		varchar(10);
cd_centro_custo_atual_w		bigint;


BEGIN 
ie_lanc_centro_custo_w	:= coalesce(obter_valor_param_usuario(1260, 4, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
cd_setor_atendimento_w	:= Obter_Dados_Usuario_Opcao(nm_usuario_p,'S');
 
if (ie_lanc_centro_custo_w = 'S') then 
	if (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') then 
		cd_centro_custo_w	:= obter_centro_custo_setor(cd_setor_atendimento_w,'C');
		 
		select	max(cd_centro_custo) 
		into STRICT	cd_centro_custo_atual_w 
		from	pls_lancamento_mensalidade 
		where	nr_sequencia	= nr_seq_lancamento_p;
		 
		if (cd_centro_custo_w IS NOT NULL AND cd_centro_custo_w::text <> '') and (coalesce(cd_centro_custo_atual_w::text, '') = '') then			 
			update	pls_lancamento_mensalidade 
			set	CD_CENTRO_CUSTO	= cd_centro_custo_w 
			where	nr_sequencia	= nr_seq_lancamento_p;
		end if;
	end if;
end if;
 
commit;
 
end;
$body$
