CREATE OR REPLACE PROCEDURE pls_usuario_lib_manual_aut ( nr_seq_guia_p bigint, nm_usuario_lib_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_w		varchar(2);


BEGIN

select	ie_status
into STRICT	ie_status_w
from	pls_guia_plano
where	nr_sequencia = nr_seq_guia_p;

if (ie_status_w = '1') then
	update	pls_guia_motivo_lib
	set	nm_usuario_liberacao = nm_usuario_lib_p
	where	nr_seq_guia = nr_seq_guia_p;

	update	pls_guia_plano
	set	nm_usuario_liberacao = nm_usuario_lib_p
	where	nr_sequencia = nr_seq_guia_p;

	update	pls_guia_plano_proc
	set	nm_usuario_liberacao = nm_usuario_lib_p
	where	nr_seq_guia = nr_seq_guia_p
	and	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '');

	update	pls_guia_plano_mat
	set	nm_usuario_liberacao = nm_usuario_lib_p
	where	nr_seq_guia = nr_seq_guia_p
	and	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
