CREATE OR REPLACE PROCEDURE pls_valida_bloq_benef_web (nr_seq_contrato_p bigint, ie_bloqueia_p INOUT text, ds_mensagem_p INOUT text) AS $body$
DECLARE


ie_bloqueia_w	varchar(1) := 'N';
ds_mensagem_w	varchar(4000);
nr_seq_regra_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_bloqueio_login_web
where	nr_seq_contrato = nr_seq_contrato_p
and	trunc(clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia, clock_timestamp())) and trunc(coalesce(dt_fim_vigencia, clock_timestamp()));

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	ds_mensagem
	into STRICT	ds_mensagem_w
	from	pls_bloqueio_login_web
	where	nr_sequencia = nr_seq_regra_w;

	ie_bloqueia_w := 'S';
end if;

ie_bloqueia_p := ie_bloqueia_w;
ds_mensagem_p := ds_mensagem_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
