SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_valida_conta_existente ( nr_seq_guia_p pls_conta.nr_seq_guia%type, nr_seq_prestador_p pls_conta.nr_seq_prestador%type, ie_conta_existe_p INOUT text, nr_seq_conta_p INOUT pls_conta.nr_sequencia%type, nr_seq_protocolo_p INOUT pls_conta.nr_seq_protocolo%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se existe conta para a guia selecionada, se existir não pode ser gerada nova
conta
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_contas_w		integer := 0;
nr_seq_conta_w		pls_conta.nr_sequencia%type;
nr_seq_protocolo_w	pls_conta.nr_seq_protocolo%type;



BEGIN


select	count(1)
into STRICT	qt_contas_w
from	pls_conta
where	nr_seq_guia 	 = nr_seq_guia_p
and	ie_status 	!= 'C';
--and    	dt_lib_complemento is null;
ie_conta_existe_p := 'N';

if ( qt_contas_w > 0 ) then
	ie_conta_existe_p := 'S';

	select	nr_sequencia,
		nr_seq_protocolo
	into STRICT	nr_seq_conta_w,
		nr_seq_protocolo_w
	from	pls_conta
	where	nr_seq_guia 	 = nr_seq_guia_p
	and	ie_status 	!= 'C'
	--and    	dt_lib_complemento is null
  LIMIT 1;
end if;

nr_seq_conta_p		:= nr_seq_conta_w;
nr_seq_protocolo_p	:= nr_seq_protocolo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
